import React, { Component } from 'react';
import './App.css';
import Products from './Products'
import TotalCost from './TotalCost'
import Axios from 'axios';


class App extends Component {




  constructor(props) {
    super(props);

    this.state = {
      loading: true,
      products: []
    }

    //Api call

    this.fetchItems();

  }


  fetchItems = () => {

    Axios.get('https://localhost:5000/api',
      {
        method: "GET",
        headers: {
          "Content-Type": "text/plain",
          "Access-Control-Allow-Origin": "https://localhost:5000/api"
        }
      })
      .then(res => {
        console.log(res.data)
        this.setState({ products: res.data.products, loading: false });
      })
      .catch(err => console.log(err))

  }


  increment = product => {
    const products = [...this.state.products];
    const index = products.indexOf(product);
    products[index] = { ...product }
    products[index].amount++;
    this.setState({ products })

    Axios.patch(`https://localhost:5000/api/${product._id}`, { amount: products[index].amount })

  }


  clearCart = product => {
    product.map((products) => {
      Axios.patch(`https://localhost:5000/api/${products._id}`, { amount: 0 }).catch(err => console.log(err))
    })




  }





  render() {
    return (


      <div>

        {this.state.loading ? <div>loading</div> : (
          <div>
            <header className="shopping-header">
              <h1>Shopping cart</h1>
              <button onClick={() => this.clearCart(this.state.products)}>Clear cart</button>
              <TotalCost className="totalCost" products={this.state.products} />
            </header>
            <div className="products-container">

              {this.state.products.map((product) =>
                <Products className="productsGrid" product={product} key={product._id} onIncrement={this.increment} />
              )}

            </div>
          </div>
        )}



      </div>
    )
  }
}

export default App;
