import React, { Component } from 'react';
import './App.css';
import Products from './Products'
import TotalCost from './TotalCost'

class App extends Component {




  constructor(props) {
    super(props);
    this.onValueChanged = this.onValueChanged.bind(this);

    this.state = {
      products: [
        {
          id: 0,
          name: 'Potato',
          amount: 0,
          price: 5


        },
        {

          id: 1,
          name: 'Tomato',
          amount: 0,
          price: 3

        },
        {

          id: 2,
          name: 'Onions',
          amount: 0,
          price: 2

        }
      ]
    }
  }



  onValueChanged(value) {



    this.setState(products => ({
      [value]:
      {
        id: this.state.products[value].id,
        name: this.state.products[value].name,
        amount: this.state.products[value].amount + 1,
        price: this.state.products[value].price
      }
    }))
    console.log(this.state.products[value]);

  }

  render() {
    return (
      <div className="App">
        <h1>Shopping App</h1>
        {this.state.products.map((product) =>
          <Products amount={product.amount} key={product.id} name={product.name} price={product.price} id={product.id} onValueChanged={this.onValueChanged} />
        )}
        <TotalCost />
      </div>
    )
  }
}

export default App;
