import React, { Component } from 'react';
import './App.css';
import Products from './Products'

class App extends Component {




  constructor(props) {
    super(props);
    this.onValueChanged = this.onValueChanged.bind(this);

    this.state = {
      products: [
        {
          id: 1,
          name: 'Potato',
          value: 0,
          price: 5
        },
        {
          id: 2,
          name: 'Tomato',
          value: 0,
          price: 3
        },
        {
          id: 3,
          name: 'Onions',
          value: 0,
          price: 2
        }
      ]
    }
  }



  onValueChanged(value) {
    console.log(value);
    const lowerCase = value.toLowerCase;

    this.setState(state => ({ [lowerCase]: state[lowerCase] + 1 }))
    console.log(this.state)
  }

  render() {
    return (
      <div className="App">
        <h1>Shopping App</h1>
        {this.state.products.map((product) =>
          <Products value={product.value} name={product.name} price={product.price} onValueChanged={this.onValueChanged} />
        )}
      </div>
    )
  }
}

export default App;
