import React, { Component } from 'react';
import './App.css';
import Products from './Products'
import TotalCost from './TotalCost'

class App extends Component {




  constructor(props) {
    super(props);

    this.state = {
      products: [
        {
          id: 0,
          name: 'Potato',
          amount: 0,
          price: 5


        },
        {

          id: 1,
          name: 'Tomato',
          amount: 0,
          price: 3

        },
        {

          id: 2,
          name: 'Onions',
          amount: 0,
          price: 2

        }
      ]
    }
  }



  increment = product => {
    const products = [...this.state.products];
    const index = products.indexOf(product);
    products[index] = { ...product }
    products[index].amount++;
    this.setState({ products })

  }

  render() {
    return (
      <div className="App">
        <h1>Shopping App</h1>
        {this.state.products.map((product) =>
          <Products product={product} key={product.id} onIncrement={this.increment} />
        )}
        <TotalCost />
      </div>
    )
  }
}

export default App;
