import React, { Component } from 'react';
import './App.css';
import Products from './Products'
import TotalCost from './TotalCost'
import Axios from 'axios';


class App extends Component {

  constructor(props) {
    super(props);

    this.state = {
      products: []
    }


  }

  async componentDidMount() {
    Axios.get('https://localhost:5000/api',
      {
        method: "GET",
        headers: {
          "Content-Type": "text/plain",
          "Access-Control-Allow-Origin": "https://localhost:5000/api"
        }
      })
      .then(res => this.setState({ products: res.data }))


  }

  increment = product => {
    const products = [...this.state.products];
    const index = products.indexOf(product);
    products[index] = { ...product }
    products[index].amount++;
    this.setState({ products })

  }





  render() {
    return (


      <div>
        <header className="shopping-header">
          <h1>Shopping cart</h1>
          <TotalCost className="totalCost" products={this.state.products} />
        </header>
        <div className="products-container">

          {this.state.products.map((product) =>
            <Products className="productsGrid" product={product} key={product.id} onIncrement={this.increment} />
          )}
          <p>{this.state.products[0].amount}</p>
        </div>

      </div>
    )
  }
}

export default App;
