import React from 'react';
import './App.css';
import { DropDownList } from 'react-widgets';

class TotalCost extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            hover: false
        }
    }

    toggleHover = () => {
        this.setState({ hover: !this.state.hover })
    }

    render() {

        return (
            <div>
                <div onMouseEnter={this.toggleHover} onMouseLeave={this.toggleHover} className="totalCost-elements">
                    <img style={{ paddingTop: "40px" }} src={require('./images/shop.png')} alt="shopping cart" width="120" height="100" ></img>
                    <p style={{ margin: "0px", paddingTop: "80px", paddingRight: "200px" }}>
                        {this.props.products[0].price * this.props.products[0].amount + this.props.products[1].price * this.props.products[1].amount + this.props.products[2].price * this.props.products[2].amount + this.props.products[3].price * this.props.products[3].amount}
                        $

                        <DropDownList />
                    </p>

                </div>
            </div>
        )
    }
}

export default TotalCost;
