import React, { Component } from 'react';
import './App.css';
import Products from './Products'
import TotalCost from './TotalCost'

class App extends Component {




  constructor(props) {
    super(props);
    this.onValueChanged = this.onValueChanged.bind(this);

    this.state = {
      products: [
        {
          id: 0,
          name: 'Potato',
          amount: 0,
          price: 5


        },
        {

          id: 1,
          name: 'Tomato',
          amount: 0,
          price: 3

        },
        {

          id: 2,
          name: 'Onions',
          amount: 0,
          price: 2

        }
      ]
    }
  }



  onValueChanged(value) {


    this.setState(products => ({ [value]: products[value].value + 1 }))



  }

  render() {
    return (
      <div className="App">
        <h1>Shopping App</h1>
        {this.state.products.map((product) =>
          <Products product={product} />
        )}
        <TotalCost />
      </div>
    )
  }
}

export default App;
