import React, { Component } from 'react';
import './App.css';
import Products from './Products'

class App extends Component {

  state = {
    products: [
      {
        id: 1,
        name: 'Potato',
        value: 0,
        price: 5
      },
      {
        id: 2,
        name: 'Tomato',
        value: 0,
        price: 3
      },
      {
        id: 3,
        name: 'Onions',
        value: 0,
        price: 2
      }
    ]
  }


  onValueChanged(value) {
    console.log(value);

    this.setState(state => ({ [value]: state[value] + 1 }))
  }

  render() {
    return (
      <div className="App">
        <h1>Shopping App</h1>
        {this.state.products.map((product) =>
          <Products value={product.value} name={product.name} price={product.price} onValueChanged={this.onValueChanged} />
        )}
      </div>
    )
  }
}

export default App;
